        <span style="color: #a6e22e">current_image</span> <span style="color: #f92672">=</span> <span style="color: #a6e22e">$sel</span><span style="color: #f8f8f2">.</span><span style="color: #a6e22e">data</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;image-id&#39;</span><span style="color: #f8f8f2">);</span>
        <span style="color: #a6e22e">$</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;#image-gallery-title&#39;</span><span style="color: #f8f8f2">)</span>
          <span style="color: #f8f8f2">.</span><span style="color: #a6e22e">text</span><span style="color: #f8f8f2">(</span><span style="color: #a6e22e">$sel</span><span style="color: #f8f8f2">.</span><span style="color: #a6e22e">data</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;title&#39;</span><span style="color: #f8f8f2">));</span>
        <span style="color: #a6e22e">$</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;#image-gallery-image&#39;</span><span style="color: #f8f8f2">)</span>
          <span style="color: #f8f8f2">.</span><span style="color: #a6e22e">attr</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;src&#39;</span><span style="color: #f8f8f2">,</span> <span style="color: #a6e22e">$sel</span><span style="color: #f8f8f2">.</span><span style="color: #a6e22e">data</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;image&#39;</span><span style="color: #f8f8f2">));</span>
        <span style="color: #a6e22e">disableButtons</span><span style="color: #f8f8f2">(</span><span style="color: #a6e22e">counter</span><span style="color: #f8f8f2">,</span> <span style="color: #a6e22e">$sel</span><span style="color: #f8f8f2">.</span><span style="color: #a6e22e">data</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;image-id&#39;</span><span style="color: #f8f8f2">));</span>
      <span style="color: #f8f8f2">}</span>

      <span style="color: #66d9ef">if</span> <span style="color: #f8f8f2">(</span><span style="color: #a6e22e">setIDs</span> <span style="color: #f92672">==</span> <span style="color: #66d9ef">true</span><span style="color: #f8f8f2">)</span> <span style="color: #f8f8f2">{</span>
        <span style="color: #a6e22e">$</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;[data-image-id]&#39;</span><span style="color: #f8f8f2">)</span>
          <span style="color: #f8f8f2">.</span><span style="color: #a6e22e">each</span><span style="color: #f8f8f2">(</span><span style="color: #66d9ef">function</span> <span style="color: #f8f8f2">()</span> <span style="color: #f8f8f2">{</span>
            <span style="color: #a6e22e">counter</span><span style="color: #f92672">++</span><span style="color: #f8f8f2">;</span>
