<span style="color: #75715e">// LEFT AND RIGHT ARROW KEYS TO SCROLL THROUGH IMAGES</span>
<span style="color: #a6e22e">$</span><span style="color: #f8f8f2">(document)</span>
  <span style="color: #f8f8f2">.</span><span style="color: #a6e22e">keydown</span><span style="color: #f8f8f2">(</span><span style="color: #66d9ef">function</span> <span style="color: #f8f8f2">(</span><span style="color: #a6e22e">e</span><span style="color: #f8f8f2">)</span> <span style="color: #f8f8f2">{</span>
    <span style="color: #66d9ef">switch</span> <span style="color: #f8f8f2">(</span><span style="color: #a6e22e">e</span><span style="color: #f8f8f2">.</span><span style="color: #a6e22e">which</span><span style="color: #f8f8f2">)</span> <span style="color: #f8f8f2">{</span>
      <span style="color: #66d9ef">case</span> <span style="color: #ae81ff">37</span><span style="color: #f92672">:</span> <span style="color: #75715e">// left</span>
        <span style="color: #66d9ef">if</span> <span style="color: #f8f8f2">((</span><span style="color: #a6e22e">modalId</span><span style="color: #f8f8f2">.</span><span style="color: #a6e22e">data</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;bs.modal&#39;</span><span style="color: #f8f8f2">)</span> <span style="color: #f92672">||</span> <span style="color: #f8f8f2">{}).</span><span style="color: #a6e22e">_isShown</span> <span style="color: #f92672">&amp;&amp;</span> <span style="color: #a6e22e">$</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;#show-previous-image&#39;</span><span style="color: #f8f8f2">).</span><span style="color: #a6e22e">is</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&quot;:visible&quot;</span><span style="color: #f8f8f2">))</span> <span style="color: #f8f8f2">{</span>
          <span style="color: #a6e22e">$</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;#show-previous-image&#39;</span><span style="color: #f8f8f2">)</span>
            <span style="color: #f8f8f2">.</span><span style="color: #a6e22e">click</span><span style="color: #f8f8f2">();</span>
        <span style="color: #f8f8f2">}</span>
        <span style="color: #66d9ef">break</span><span style="color: #f8f8f2">;</span>

      <span style="color: #66d9ef">case</span> <span style="color: #ae81ff">39</span><span style="color: #f92672">:</span> <span style="color: #75715e">// right</span>
        <span style="color: #66d9ef">if</span> <span style="color: #f8f8f2">((</span><span style="color: #a6e22e">modalId</span><span style="color: #f8f8f2">.</span><span style="color: #a6e22e">data</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;bs.modal&#39;</span><span style="color: #f8f8f2">)</span> <span style="color: #f92672">||</span> <span style="color: #f8f8f2">{}).</span><span style="color: #a6e22e">_isShown</span> <span style="color: #f92672">&amp;&amp;</span> <span style="color: #a6e22e">$</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;#show-next-image&#39;</span><span style="color: #f8f8f2">).</span><span style="color: #a6e22e">is</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&quot;:visible&quot;</span><span style="color: #f8f8f2">))</span> <span style="color: #f8f8f2">{</span>
          <span style="color: #a6e22e">$</span><span style="color: #f8f8f2">(</span><span style="color: #e6db74">&#39;#show-next-image&#39;</span><span style="color: #f8f8f2">)</span>
            <span style="color: #f8f8f2">.</span><span style="color: #a6e22e">click</span><span style="color: #f8f8f2">();</span>
        <span style="color: #f8f8f2">}</span>
        <span style="color: #66d9ef">break</span><span style="color: #f8f8f2">;</span>

      <span style="color: #66d9ef">default</span><span style="color: #f92672">:</span>
        <span style="color: #66d9ef">return</span><span style="color: #f8f8f2">;</span> <span style="color: #75715e">// exit this handler for other keys</span>
    <span style="color: #f8f8f2">}</span>
