  <span style="color: #75715e">// position: absolute;</span>
  <span style="color: #75715e">// left: 45%;</span>
  <span style="color: #75715e">// top: 5%;</span>
<span style="color: #f8f8f2">}</span>

<span style="color: #75715e">//WEATHER STYLING//</span>

<span style="color: #f8f8f2">#weather</span> <span style="color: #f8f8f2">{</span>
  <span style="color: #a6e22e">font-size</span><span style="color: rgb(231, 95, 85)">:</span> <span style="color: #ae81ff">18</span><span style="color: #66d9ef">px</span><span style="color: #f8f8f2">;</span>
<span style="color: #f8f8f2">}</span>

<span style="color: #f8f8f2">#facts{</span>
  <span style="color: #a6e22e">font-size</span><span style="color: rgb(231, 95, 85)">:</span> <span style="color: #ae81ff">18</span><span style="color: #66d9ef">px</span><span style="color: #f8f8f2">;</span>
  <span style="color: #a6e22e">text-align</span><span style="color: rgb(231, 95, 85)">:</span> <span style="color: #66d9ef">center</span><span style="color: #f8f8f2">;</span>
  <span style="color: #a6e22e">position</span><span style="color: rgb(231, 95, 85)">:</span> <span style="color: #66d9ef">fixed</span><span style="color: #f8f8f2">;</span>
  <span style="color: #a6e22e">left</span><span style="color: rgb(231, 95, 85)">:</span> <span style="color: #ae81ff">0</span><span style="color: #f8f8f2">;</span>
  <span style="color: #a6e22e">bottom</span><span style="color: rgb(231, 95, 85)">:</span> <span style="color: #ae81ff">50</span><span style="color: #66d9ef">px</span><span style="color: #f8f8f2">;</span>
  <span style="color: #a6e22e">width</span><span style="color: rgb(231, 95, 85)">:</span> <span style="color: #ae81ff">100</span><span style="color: #66d9ef">%</span><span style="color: #f8f8f2">;</span>
<span style="color: #f8f8f2">}</span>

<span style="color: #75715e">//MIRROR MIDDLE SECTION</span>
<span style="color: #f8f8f2">#mirrorPart</span> <span style="color: #f8f8f2">{</span>
  <span style="color: #a6e22e">width</span><span style="color: rgb(231, 95, 85)">:</span> <span style="color: #ae81ff">100</span><span style="color: #66d9ef">%</span><span style="color: #f8f8f2">;</span>
  <span style="color: #a6e22e">min-height</span><span style="color: rgb(231, 95, 85)">:</span> <span style="color: #a6e22e">calc</span><span style="color: #f8f8f2">(</span><span style="color: #ae81ff">100</span><span style="color: #66d9ef">vh</span> <span style="color: rgb(231, 95, 85)">-</span> <span style="color: #f8f8f2">(</span><span style="color: #ae81ff">1350</span><span style="color: #66d9ef">px</span><span style="color: #f8f8f2">));</span>
<span style="color: #f8f8f2">}</span>

<span style="color: #75715e">//MUSIC STYLING//</span>
 <span style="color: #f8f8f2">#spotify</span> <span style="color: #f8f8f2">{</span>
